//------------------------------------------------------------------------------
// Contact us
//------------------------------------------------------------------------------
#contact-us-page{max-width:600px;}
.custom-page.contact-us{display:flex;flex-flow: row wrap;justify-content:space-between;max-width:1100px;margin:0 auto 50px auto;} 
.custom-page.contact-us #contact-us-page{
	width:33%;
	order:1;
	margin:0;
	height:fit-content;
	padding:0;
	max-width:unset;
}
.custom-page.contact-us #contact-us-page .extra-div{background-color: #EDEDED;
    padding:15px 25px;width:100%;}
.custom-page.contact-us #contact-us-page .form-field{margin: 0 0 25px;}

.custom-page.contact-us #contact-us-page textarea{height:6rem}
.custom-page.contact-us  #contact-us-page .form-actions .button{width:100%;margin:0;background-color: #6aaf0a;color: #fff;border:1px solid #6aaf0a}



.contactus-info,.blackspace,.quick-contact{display:none;}



.custom-page.contact-us  #contact-us-page .form-label{color: #6aaf0a !important;
    font-size: 14px;
    padding: 5px 0 !important;
    width: 100% !important;
    text-align: left !important;text-transform: uppercase;font-weight: 700;}




.custom-page.contact-us .quick-contact {
    width: 100%;
    color: #6aaf0a;
    font-size: 18px;
    font-weight: 800;
    text-transform: uppercase;
	display:block;
	text-align: left;
}

.custom-page.contact-us .quick-contact span {
    border-bottom: 2px solid #6aaf0a;
    padding-right: 50px;
    padding-bottom: 5px;
}





.custom-page.contact-us .contactus-info{
	width:61%;
	order:2;
    display: flex;
    flex-flow: row wrap;text-align: left;
    justify-content: space-between;
	height: fit-content;
}

.contact-sec2-sub {
    margin-bottom: 50px;
	    width: 45%;
		
}
.contact-sec2-title {
    color: #3d3d3d;
    font-size: 20px;
    font-weight: 800;
    border-bottom: 1px solid #ccc;
    padding-bottom: 10px;
    margin: 0 0 15px;
}

.contact-sec2-text {
    color: #303030;
    font-size: 16px;
    font-weight: 500;
    margin: 0px;
}


@media only screen and (max-width: 1100px) {

.custom-page.contact-us{;max-width:calc(100% - 60px);} 


}

@media only screen and (max-width: 991px) {
.custom-page.contact-us #contact-us-page{width:48%;}
.custom-page.contact-us .contactus-info{width:48%;}
.contact-sec2-sub {width:100%;margin-bottom: 30px;}
}

@media only screen and (max-width: 767px) {

.custom-page.contact-us #contact-us-page{width:100%;margin-bottom:50px;}
.custom-page.contact-us .contactus-info{width:100%;}
.contact-sec2-sub {margin-bottom: 30px;}
}


