store-custom/components/products/options:
{{#and (if this.values.0.label '!==' '(Yes)') (if this.values.0.label '!==' '(Included)') }} 

	<div class="form-field" data-product-attribute="set-radio">
		<label class="form-label form-label--alternate form-label--inlineSmall">
			{{ this.display_name }}:

			{{#if required}}
				<small>{{lang 'common.required'}}</small>
			{{/if}}
		</label>

		{{#each this.values}}
			<input
				class="form-radio"
				type="radio"
				id="attribute_radio_{{../id}}_{{id}}"
				name="attribute[{{../id}}]"
				value="{{id}}"
				{{#if selected}}
					checked
					data-default
				{{/if}}
				{{#if ../required}}required{{/if}}>
			<label data-product-attribute-value="{{id}}" class="form-label" for="attribute_radio_{{../id}}_{{id}}">{{this.label}}</label>
		{{/each}}
	</div>

{{else}}

	<div class="form-field" data-product-attribute="input-checkbox">
		<label class="form-label form-label--alternate form-label--inlineSmall">
			{{display_name}}:

			{{#if required}}
				<small>{{lang 'common.required'}}</small>
			{{/if}}
		</label>

		{{#each this.values}}
			<input
			   class="form-checkbox"
				type="checkbox"
				id="attribute_radio_{{../id}}_{{id}}"
				name="attribute[{{../id}}]"
				value="{{id}}"
				{{#if selected}}
					checked
					data-default
				{{/if}}
				{{#if ../required}}required{{/if}}>
			<label data-product-attribute-value="{{id}}" class="form-label" for="attribute_radio_{{../id}}_{{id}}"></label>
		{{/each}}
	</div>

{{/and}}
